import {useEffect, useState} from 'react';
import AddText from './components/AddText';
const App = () => {
 const[data, setData] = useState([])

 useEffect(() =>{
   // get api implement
   const url = 'https://jsonplaceholder.typicode.com/comments'
   fetch(url)
  .then(response =>
   response.json()
   )
   .then(json => 
    setData(json)
    )

 .catch(e =>{ 
   console.log('e', e)})
}, [])

//post and put api implement

function postPutEvent(){
  const data = {
    name: 'Seema',
    mobile: '767676786',
    designation: 'developer',
    pin: '45678'
  }
  const url = 'https://jsonplaceholder.typicode.com/comments'
   fetch(url, {
     method: 'POST',
     headers: {
       'Content-Type': 'application/json',
       'Access-Control-Allow-Origin': '*'
     },
     body: JSON.stringify(data)
   })
  .then(response =>
   response.json()
   )
   .then(json => 
    setData(json)
    )

 .catch(e =>{ 
   console.log('e', e)})
}

  return (

    <div className='center'>
      <h1>Welcome</h1>
       {data.filter(user => user.id > 100).map(item=>(
         <div  key={item.id}>
         <p>{item.email} </p>
         <p>{item.id} </p>
         </div>
       ))}
      <button onClick={postPutEvent}>submit</button>
    </div>
  );
}

export default App;
