import React from 'react'

const AddText = ({inputChange, addPost, posts, showModal,  }) => {
  return (
    <div>
      
  <input type="text" name='id' onChange={inputChange}/>
  <button onClick={addPost}>Add Post</button>
  {posts.map(post=>(
    <h2 key = 'id'>{post}</h2>
  ))}
  {showModal && <div className='center'>There are good memories</div>}
 
   {obj.map(item => (
     <div>
     <ul className='card' key={item.id}>
     <li>Name: {item.name}</li>
     <li>Age: {item.age}</li>
     <li>profession: {item.profession}</li>
     <li>Id: {item.id}</li>
     <li>time: {item.time}</li>
     </ul>
     <button onClick ={()=>deletePost(item.id)}>delete</button>
     </div>
   ))}

    </div>
  )
}

export default AddText
