import {useState} from 'react';
const App = () => {
  const[sum, setSum] = useState(0);

  function increment(){
    setSum(prevSum =>
      prevSum + 1) 
  }

  return (

    <div>
        <p>{sum}</p>

     <button>
       increment
     </button>
     <button>
       decrement
     </button>

    </div>
  );
}

export default App;
