import {useEffect, useState} from 'react';

const App = () => {
const[inputText, setInputText] = useState(null);
const[data, setData] = useState([]);

useEffect(()=>{


    fetch(
      `https://api.github.com/users`
    )
      .then((res) => res.json())
      .then((data) => {
        setData(data);
      });


}, [inputText])

function clickHandler(){
  fetch(
    `https://api.github.com/users/${inputText}`
  )
    .then((res) => res.json())
    .then((data) => {
      setData(data);
    });
}

  return (

    <div className='App'>
       <div className='form'>
         <form onSubmit={(e)=> e.preventDefault()}>
            <input type="search" onChange={(e)=>setInputText(e.input.value)}/>
            <button onClick={clickHandler}>Submit</button>
       </form>
       </div>

       <div className='user-info'>
         {(inputText ===user.login ) && data.map(user=> (
           <ul>
             <li>username: {user.login}</li>
             <li><img src={user.avatar_url}/></li>
             <li><a href={user.html_url} target='_blank' >profile</a></li>
             <li></li>
           </ul>
         ))}
       </div>
    </div>
  );
}

export default App;
