import {useEffect, useState} from 'react';

const App = () => {
const[buttonName, setButtonName] = useState(true)
const[users, setUsers] = useState([
  {name: 'maria', id:1},
  {name: 'nata', id:2},
  {name: 'giorgi', id:3},

])
function hideText(){
  setButtonName(!buttonName)
}

function deleteUser(e){
    const filteredArr = users.filter(user=> 
      user.id !== e
      )

      setUsers(filteredArr)
}

  return (

    <div className='App'>
      <button onClick={hideText}>{buttonName ? 'Hide' : 'Show'}</button>
     {buttonName &&  users.map(user=>(
       <ul key={user.id}>
         <li>{user.name}</li>
         <li><button onClick={()=> deleteUser(user.id)}>Delete</button></li>
       </ul>

     ))}
    </div>
  );
}

export default App;
