import {useEffect, useState} from 'react';
import AddText from './components/AddText';
const App = () => {

  const[text, setText] = useState('');
  const[posts, setPosts] = useState([]);
  const[showModal, setShowModal] = useState(true);
  const[jsonAp, setJasonAp] = useState();
  const[obj, setObj] = useState([
    {
      name: 'maria', age: 23, profession: 'web developer', id: 1
    },  
    {
      name: 'Jack', age: 29, profession: 'Intern', id: 2
    },
    {
      name: 'Nata', age: 33, profession: 'Nurse', id: 3
    }

  ])


  useEffect(()=>{
    fetch('https://api.github.com/users')
    .then(res=> res.json())
    .then(result=> setJasonAp(result))

  }, [])

  function deletePost(e){
     const removed = obj.filter(item => e !== item.id)

     setObj(removed)
  }

 function inputChange(e){
   setText(e.target.value)
 }

 function addPost(){
   obj.time = 23
   console.log(obj)
 }
  return (

    <div>
  <AddText inputChange = {inputChange}
   addPost={inputChange} posts={posts}
    showModal={showModal} obj={obj}
    deletePost={deletePost} />
    </div>
  );
}

export default App;
