import {useState} from 'react';
const App = () => {
  const[sum, setSum] = useState(0);

  function increment(){
    setSum(prevSum =>
      prevSum + 1) 
  }

  function decrement(){
    setSum(prevSum =>
      prevSum - 1)
  }

  function reset(){
    setSum(0);
  }

  return (

    <div>
        <p>{sum}</p>

     <button onClick={increment}>
       increment
     </button>
     <button onClick={decrement}>
       decrement
     </button>

    </div>
  );
}

export default App;
