import {useEffect, useState} from 'react';

const App = () => {
const[inputText, setInputText] = useState('');
const[data, setData] = useState([]);

useEffect(()=>{
  fetch(`https://api.github.com/users/${inputText}`)
  .then(response =>
    response.json())
  .then(data=>
    setData(data)
    )
}, [inputText])

function changeInput(e){
  setInputText(e.target.value)
}
  return (

    <div className='App'>
       <div className='form'>
            <input type="search" onChange={changeInput}/>
       </div>

       <div className='user-info'>
         {data.map(user=>(
           <ul>
             <li><img src={user.avatar_url}/> </li>
           </ul>
          ))}
       </div>
    </div>
  );
}

export default App;
