import {useEffect, useState} from 'react';

export default function FormValidation() {
    const[email, setEmail] = useState('');
    const[password, setPassword] = useState('');
    const[emailDirty, setEmailDirty] = useState(false)
    const[passwordDirty, setPasswordDirty] = useState(false)
    const[emailError, setEmailError] = useState('Email Can Not Be Empty')
    const[passwordError, setPasswordError] = useState('Password Can Not Be Empty')
    const[formValid, setFormValid] = useState(false)

    useEffect(()=>{
        if(emailError || passwordError) {
            setFormValid(false)
        }
        else{
            setFormValid(true)
        }
    }, [emailError, passwordError])
    const emailHandler = (e)=>{
        setEmail(e.target.value)
        const re = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        if(!re.test(String(e.target.value).toLowerCase())){
            setEmailError('Wrong Email')
        }
        else{
            setEmailError('')
        }
    }

    const passwordHandler =(e)=>{
        setPassword(e.target.value)
        if(e.target.value.length < 3 || e.target.value.length > 8){
            setPasswordError('password must be longer than 3 and lesser than 8')
            if(!e.target.value){
                setPasswordError('Password Can Not Be Empty')
            }
        }
        else{
            setPasswordError('')
        }

    }

    function blurHandler(e){
        switch(e.target.name){
            case 'email':
                setEmailDirty(true)
                break
            case 'password': 
                setPasswordDirty(true)
                break
        }

    }
    return (
     <form>
         <h1>registration</h1>
         {(emailDirty && emailError) && <div style={{color: 'red'}}>{emailError}</div>}
         <input onChange={emailHandler} value={email} onBlur={(e)=> blurHandler(e)} name='email' type='text' placeholder='Enter your'/>
         {(passwordError && passwordDirty) && <div style={{color: 'red'}}>{passwordError}</div>}
         <input onChange={passwordHandler} value={password} onBlur={(e)=> blurHandler(e)}name='password' type='password'placeholder='Enter your password...'/>
         <button disabled = {!formValid}type='submit'> Registration</button>
     </form>
    )
}
