import {useState} from 'react';

export default function FormValidation() {
    const[email, setEmail] = useState('');
    const[password, setPassword] = useState('');
    const[emailDirty, setEmailDirty] = useState(true)
    const[passwordDirty, setPasswordDirty] = useState(true)
    const[emailError, setEmailError] = useState('Email Can Not Be Empty')
    const[passwordError, setPasswordError] = useState('Password Can Not Be Empty')
    
    return (
     <form>
         <h1>registration</h1>
         {(emailDirty && passwordDirty) && <div>{emailError} and {passwordError}</div>}
         <input name='email' type='text' placeholder='Enter your'/>
         <input name='password' type='password'placeholder='Enter your password...'/>
         <button type='submit'> Registration</button>
     </form>
    )
}
