import {useState} from 'react';
const App = () => {
  const[sum, setSum] = useState(0);

  function increment(){
    setSum(prevSum =>
      prevSum + 1) 
  }

  function decrement(){
   if(sum > 0){
    setSum(prevSum =>
      prevSum - 1)
    }
  }

  function reset(){
    setSum(0);
  }

  function increase(){
    setSum(prevSum=>
      prevSum * 2)
  }

  return (

    <div>
        <p>{sum}</p>

     <button onClick={increment}>
       increment
     </button>
     <button onClick={decrement}>
       decrement
     </button>
    <button onClick={reset}>
      reset
    </button>

  <button>Increase Twice</button>
    </div>
  );
}

export default App;
